using System.Drawing;namespace kanavrt.Model.Settings.Grid.Table {	public abstract class AbstractTableModel {		public bool[] VowelsLineLookup, ConsonantsLineLookup;		public HashSet<string>[] VowelsLineSetLookup, ConsonantsLineSetLookup;		public HashSet<string> ConsonantsSet, VowelsSet;		public bool isConsonants, isVowels;		public int vowelsCount, consonansCount;		public AbstractTableModel(int vowelsCount, int consonansCount, bool isConconants, bool isVowels) {			this.vowelsCount = vowelsCount;			this.consonansCount = consonansCount;			VowelsLineLookup = Enumerable.Repeat(isVowels, vowelsCount).ToArray();			ConsonantsLineLookup = Enumerable.Repeat(isConconants, consonansCount).ToArray();			VowelsLineSetLookup = VowelsLineSetLookupSet();			if (VowelsLineSetLookup.Length != vowelsCount) throw new("[ERROR] Invalid lookup table size");			ConsonantsLineSetLookup = ConsonantsLineSetLookupSet();			if (VowelsLineSetLookup.Length != vowelsCount) throw new("[ERROR] Invalid lookup table size");			ConsonantsSet = ConsonantsSetSet();			VowelsSet = VowelsSetSet();			this.isConsonants = isConconants;			this.isVowels = isVowels;		}		public abstract HashSet<string>[] VowelsLineSetLookupSet();		public abstract HashSet<string>[] ConsonantsLineSetLookupSet();		public abstract HashSet<string> ConsonantsSetSet();		public abstract HashSet<string> VowelsSetSet();	}}